sourcesContent:
import React, { Component } from "react";
import Board from "./Board";
import ConsWarn from "./Console";
import "./Simulator.css";
import { connect } from "react-redux";
import getTaskList from "../features/taskSlice";
import Task from "./Task";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      note: 0,
      rest: false,
      // buttonBoolSMD: true,
      buttonBoolSMD: false,

      keySig: null,
      up: null,
      down: null,

      clef: null,

      alphabet: ["A", "B", "C", "D", "E", "F", "G"],
      solfeggio: ["do", "re", "mi", "fa", "so", "la", "ti"],
      places: [
        { alp: 0, sol: "", place: 0, accidentals: 0 },
        { alp: 0, sol: "", place: 1, accidentals: 0 },
        { alp: 0, sol: "", place: 2, accidentals: 0 },
        { alp: 0, sol: "", place: 3, accidentals: 0 },
        { alp: 0, sol: "", place: 4, accidentals: 0 },
        { alp: 0, sol: "", place: 5, accidentals: 0 },
        { alp: 0, sol: "", place: 6, accidentals: 0 },
        { alp: 0, sol: "", place: 7, accidentals: 0 },
        { alp: 0, sol: "", place: 8, accidentals: 0 },
        { alp: 0, sol: "", place: 9, accidentals: 0 },
        { alp: 0, sol: "", place: 10, accidentals: 0 },
        { alp: 0, sol: "", place: 11, accidentals: 0 },
      ],

      accidental: "#",

      handle: 0,
      viewOption: false,
      tempo: 120,
      volume: 100,
      playIcon: true,
      metronome: false,
      pauseIcon: false,
      consWarn: [{ start: true, warns: 0 }],

      workSheet: false,
      task: [
        { kind: "clef", what: "G", complete: false },
        { kind: "keySig", what: "C", complete: false },
        { kind: "timeSig", whatUp: 4, whatDown: 4, complete: false },
      ],
      measureTask: [],
      taskRender: [],
      completed: 0,
      percentage: 0,

      taskX: 0,
      taskClef: 0,
      taskUp: 0,
      taskDown: 0,
      taskKeySig: 0,
    };
    this.viewOptionOn = this.viewOptionOn.bind(this);
    this.viewOptionOff = this.viewOptionOff.bind(this);
  }

  runMe = (x, clef, up, down, keySig) => {
    // let measureTask = this.state.measureTask;
    // let a = 1;
    let taskX = this.state.taskX;
    let taskClef = this.state.taskClef;
    let taskUp = this.state.taskUp;
    let taskDown = this.state.taskDown;
    let taskKeySig = this.state.taskKeySig;
    console.log((x, clef, up, down, keySig));

    this.setState({
      taskX: x,
      taskClef: clef,
      taskUp: up,
      taskDown: down,
      taskKeySig: keySig,
    });

    console.log(
      this.state.taskX,
      this.state.taskClef,
      this.state.taskUp,
      this.state.taskDown,
      this.state.taskKeySig
    );
  };

  task = () => {
    let task = this.state.task;
    let measureTask = this.state.measureTask;
    let taskRender = this.state.taskRender;
    let completed = this.state.completed;

    if (task !== 0) {
      for (let y = 0; y < measureTask.length; y++) {
        let c = 0;

        for (let i = 0; i < task.length; i++) {
          console.log(i);
          if (task[i].kind === "clef") {
            console.log("clef");
            let kindClef = "G";
            if (measureTask[y].clef === "ð„¢") {
              kindClef = "F";
            }

            if (task[i].what === kindClef) {
              console.log("task complete");
              task[i].complete = true;

              taskRender[i] = "Make a " + task[y].what + " clef  - completed";
            } else {
              console.log("task not complete");
              task[i].complete = false;
              taskRender[i] =
                "Make a " + task[y].what + " clef  - not complete";
            }
            continue;
          }

          if (task[i].kind === "keySig") {
            console.log("key");
            if (task[i].what === measureTask[y].keySig) {
              console.log("task complete");
              task[i].complete = true;

              taskRender[i] =
                "Make key of " + this.state.task[y].what + "  - completed";
            } else {
              console.log("task not complete");
              task[i].complete = false;
              taskRender[i] = "Make key of " + task[y].what + " - not complete";
            }
            continue;
          }

          if (task[i].kind === "timeSig") {
            console.log("time");

            if (task[i].whatUp === measureTask[y].up) {
              console.log("task complete");
              c++;
              taskRender[i] =
                "Make a " +
                task[y].whatUp +
                " " +
                task[y].whatUp +
                " time signature clef  - completed";
            } else {
              console.log("task not complete");
              taskRender[i] =
                "Make a " +
                task[y].whatUp +
                " " +
                task[y].whatUp +
                " time signature clef  - not complete";
            }

            if (task[i].whatDown === measureTask[y].down) {
              console.log("task complete");
              c++;
              taskRender[i] =
                "Make a " +
                task[y].whatUp +
                " " +
                task[y].whatDown +
                " time signature clef  - not complete";
            } else {
              console.log("task not complete");
              taskRender[i] =
                "Make a " +
                task[y].whatUp +
                " " +
                task[y].whatDown +
                " time signature clef  - completed";
            }
            if (c === 2) {
              task[i].complete = true;
            } else {
              task[i].complete = false;
            }
            continue;
          }
        }
        completed = 0;
        for (let a = 0; a < task.length; a++) {
          if (task[a].complete === true) {
            console.log("completed + 1");
            completed++;
            this.setState({ completed });
          }
        }

        console.log(completed);
        let percentage = this.state.percentage;
        percentage = 0;
        percentage = (this.state.completed / this.state.task.length) * 100;
        this.setState({ task, taskRender, completed, percentage });
        console.log(percentage);
      }
    }
  };

  taskRender = () => {
    if (this.state.task.length !== 0) {
      let task = this.state.task;
      let taskRender = this.state.taskRender;
      let nC = "  - not complete";

      for (let y = 0; y < task.length; y++) {
        console.log(task[y], y);
        if (task[y].kind === "clef") {
          taskRender.push("Make a " + task[y].what + " clef" + nC);
        }

        if (task[y].kind === "keySig") {
          taskRender.push("Make key of " + task[y].what + nC);
        }

        if (task[y].kind === "timeSig") {
          taskRender.push(
            "Make a " +
              task[y].whatUp +
              " " +
              task[y].whatDown +
              " time signature" +
              nC
          );
        }
      }

      this.setState({ taskRender });
      console.log(this.state.taskRender);
    }
  };
  consoleWarningTS = (
    type,
    measure,
    error,
    up,
    down,
    barLength,
    totalNoteValue
  ) => {
    console.log(
      "measure =",
      measure,
      "type =",
      type,
      "error =",
      error,
      up,
      down,
      barLength,
      totalNoteValue
    );
    let consWarn = this.state.consWarn;
    consWarn[0].warn = true;

    if (consWarn.length > 1) {
      for (let a = 1; a <= consWarn.length - 1; a++) {
        if (consWarn[a].measure === measure && consWarn[a].type === type) {
          if (
            consWarn[a].error === 0 ||
            consWarn[a].error === 1 ||
            consWarn[a].error === 2 ||
            consWarn[a].error === 3 ||
            consWarn[a].error === 4 ||
            consWarn[a].error === 5
          ) {
            // if  error = 1 or 2 or 3 exist, update warning------
            consWarn[a].type = type;
            consWarn[a].error = error;
            consWarn[a].measure = measure;
            consWarn[a].barLength = barLength;
            consWarn[a].totalNoteValue = totalNoteValue;
            consWarn[a].up = up;
            consWarn[a].down = down;
            this.setState({ consWarn });
            console.log(this.state.consWarn);
            return;
          }
        }
      }
      //add new warning
      consWarn.push({
        type: type,
        error: error,
        measure: measure,
        barLength: barLength,
        totalNoteValue: totalNoteValue,
        show: false,
        up: up,
        down: down,
      });
    } else {
      consWarn.push({
        type: type,
        error: error,
        measure: measure,
        barLength: barLength,
        totalNoteValue: totalNoteValue,
        show: false,
        up: up,
        down: down,
      });
    }

    this.setState({
      consWarn,
    });
    this.findConsWarnLength();
    console.log(this.state.consWarn);
  };
  consoleWarningClef = (type, measure, error) => {
    console.log("measure =", measure, "type =", type, "error =", error);
    let consWarn = this.state.consWarn;
    consWarn[0].warn = true;

    if (consWarn.length > 1) {
      console.log("measure =", measure, "type =", type, "error =", error);
      for (let a = 1; a <= consWarn.length - 1; a++) {
        if (consWarn[a].measure === measure && consWarn[a].type === type) {
          console.log("measure =", measure, "type =", type);
          // edit type = type; measure = measure
          if (consWarn[a].error === 6 || consWarn[a].error === 7) {
            // if  error = 7 exist, update warning------
            consWarn[a].type = type;
            consWarn[a].error = error;
            consWarn[a].measure = measure;
            this.setState({ consWarn });
            console.log(this.state.consWarn);
            return;
          }
        }
      }
      //add new warning
      console.log("add warning for ----------- measure and type only");
      consWarn.push({
        type: type,
        error: error,
        measure: measure,
        show: false,
      });
    } else {
      console.log("add warning for ----------- new 3 parameters ");
      consWarn.push({
        type: type,
        error: error,
        measure: measure,
        show: false,
      });
    }

    this.setState({
      consWarn,
    });
    this.findConsWarnLength();
    console.log(this.state.consWarn);
  };
  consoleWarningKS = (type, measure, error, accidental) => {
    let consWarn = this.state.consWarn;
    consWarn[0].warn = true;

    if (consWarn.length > 1) {
      console.log("measure =", measure, "type =", type, "error =", error);
      for (let a = 1; a <= consWarn.length - 1; a++) {
        if (
          consWarn[a].measure === measure &&
          consWarn[a].type === type &&
          consWarn[a].error === error
        ) {
          console.log("measure =", measure, "type =", type);
          // edit type = type; measure = measure
          if (
            consWarn[a].error === 8 ||
            consWarn[a].error === 9 ||
            consWarn[a].error === 10 ||
            consWarn[a].error === 11 ||
            consWarn[a].error === 12
          ) {
            // if  error = 8 exist, update warning------
            consWarn[a].type = type;
            consWarn[a].error = error;
            consWarn[a].measure = measure;
            consWarn[a].accidental = accidental;
            this.setState({ consWarn });
            console.log(this.state.consWarn);
            return;
          }
        }
      }
      //add new warning
      console.log("add warning for ----------- measure and type only");
      consWarn.push({
        type: type,
        error: error,
        measure: measure,
        accidental: accidental,
        show: false,
      });
    } else {
      console.log("add warning for ----------- new 3 parameters ");
      consWarn.push({
        type: type,
        error: error,
        measure: measure,
        accidental: accidental,
        show: false,
      });
    }

    this.setState({ consWarn });
    this.findConsWarnLength();
    console.log(this.state.consWarn);
  };
  readMore = (t, m, e) => {
    console.log(m, t, e);
    let consWarn = this.state.consWarn;
    for (let a = 1; a <= consWarn.length - 1; a++) {
      console.log(a, consWarn[a].show);
      if (
        consWarn[a].measure === m &&
        consWarn[a].type === t &&
        consWarn[a].error === e
      ) {
        if (consWarn[a].show === false) {
          consWarn[a].show = true;
          this.findConsWarnLength();
          return;
        } else {
          consWarn[a].show = false;
          this.findConsWarnLength();
          return;
        }
      }
    }
  };
  removeWarning = (type, measure, error) => {
    let consWarn = this.state.consWarn;

    for (let a = 1; a <= consWarn.length - 1; a++) {
      console.log(a, consWarn[a].show);

      if (consWarn[a].measure === measure && consWarn[a].type === 1) {
        if (
          consWarn[a].error === 1 ||
          consWarn[a].error === 2 ||
          consWarn[a].error === 3
        ) {
          consWarn.splice(a, 1);

          this.findConsWarnLength();
          return;
        }
      }

      if (
        consWarn[a].measure === measure &&
        consWarn[a].type === type &&
        consWarn[a].error === error
      ) {
        if (consWarn[a].show === false) {
          consWarn.splice(a, 1);

          this.findConsWarnLength();
          return;
        } else {
          consWarn[a].show = false;
          consWarn.splice(a, 1);

          return;
        }
      }
    }

    // for (let a = 1; a <= consWarn.length -1  ;a++) {
    //    console.log(consWarn[a].measure, measure);

    //   if (type === 1) {
    //     if (consWarn[a].measure === measure) {
    //       console.log("measure = measure");
    //       consWarn.splice(a, 1)

    //     }
    //   }
    //   if (type === 2) {
    //     if (consWarn[a].measure === measure) {
    //       console.log("measure = measure");
    //       consWarn.splice(a, 1)

    //     }
    //   }
    //     this.setState({ consWarn })
    // }
    // this.findConsWarnLength();
  };
  recalcuConsoleMeasure = (measure) => {
    let consWarn = this.state.consWarn;
    console.log(consWarn.length);

    if (consWarn.length - 1 !== 1) {
      for (let a = consWarn.length - 1; a > 0; a--) {
        if (consWarn[a].type === 1) {
          if (consWarn[a].measure === measure) {
            console.log("measure = measure");
            consWarn.splice(a, 1);

            this.setState({
              consWarn,
            });
          }
        }
      }
    }
    this.findConsWarnLength();
  };
  findConsWarnLength() {
    let consWarn = this.state.consWarn;
    let a = consWarn.length;
    consWarn[0].warns = a;
    this.setState({
      consWarn,
    });
    console.log(this.state.consWarn[0].warns);
  }
  handleChange = (event) => {
    this.setState({
      keySig: event.target.value,
    });
  };
  renderMe() {
    let places = this.state.places;
    let alphabet = this.state.alphabet;
    let KeySig = this.state.keySig;

    let b = alphabet.findIndex((alphabet) => {
      if (alphabet === KeySig) {
        return true;
      } else {
        return false;
      }
    });

    let i = 0;
    let j = 0;
    let d = 0;

    while (i < 12) {
      let c = b + d;

      if (
        i === 0 ||
        i === 2 ||
        i === 4 ||
        i === 5 ||
        i === 7 ||
        i === 9 ||
        i === 11
      ) {
        if (c < 7) {
          places[i].alp = c;
          d++;
        } else {
          c = 0;
          places[i].alp = j;
          j++;
        }
        i++;
      } else {
        places[i].alp = "none";
        i++;
      }
    }

    this.setState({
      places: places,
    });
    console.log(this.state.places);
  }
  keySigUp = (event) => {
    this.setState({
      up: parseInt(event.target.value),
    });
  };
  keySigDown = (event) => {
    this.setState({
      down: parseInt(event.target.value),
    });
  };
  viewOptionOn() {
    this.setState({
      viewOption: true,
    });
  }
  viewOptionOff() {
    this.setState({
      viewOption: false,
    });
  }
  insertStartingMeasureDisabler = (swt) => {
    this.setState({
      buttonBoolSMD: swt,
    });
  };
  playerIcon = (playIcon) => {
    console.log("player is on", playIcon);
    let plI = this.state.playIcon;
    let paI = this.state.pauseIcon;
    if (playIcon === true) {
      plI = false;
      paI = true;
    } else {
      plI = true;
      paI = false;
    }
    this.setState({ playIcon: plI, pauseIcon: paI });
  };
  tempo(event) {
    this.setState({
      tempo: parseInt(event.target.value),
    });
  }
  Metronome() {
    console.log("metronome");
    let met = null;
    if (this.state.metronome === false) {
      met = true;
    } else {
      met = false;
    }
    this.setState({ metronome: met });
    console.log(this.state.metronome);
  }
  volume(event) {
    this.setState({
      volume: parseInt(event.target.value),
    });
  }

  componentDidMount() {
    document.onkeydown = this.onKeyDown;
    this.taskRender();
    // this.props.getTaskList(this.props.token);
  }
  // onKeyDown = (e) => {
