  //       document.getElementById("startPauseSwitch").click();
  //       break;
  //     case 77:
  //       document.getElementById("clockStop").click();
  //       break;

  //     default:
  //   }
  // };

  render() {
    let consWarn = this.state.consWarn.map((consWarn, index) => {
      return (
        <ConsWarn
          consWarn={consWarn}
          key={index}
          findWarning={this.findWarning}
          readMore={this.readMore}
          removeWarning={this.removeWarning}
        />
      );
    });

    let taskRender = this.state.taskRender.map((taskRender, index) => {
      return (
        <li key={index}>
          {taskRender}
          <br />
        </li>
      );
    });

    return (
      <div className="canvas">
        {/* ----------------------------------- Tools & Measurements----------------------------------- */}
        <div className="tool">
          <div className="container">
            <p className="container-header">Tools</p>
            <div className="box-devider1">
              <div className="box-devider">
                <button
                  disabled={this.state.buttonBoolSMD}
                  onClick={() => this.insertStartingMeasure()}
                >
                  Insert Measurement
                </button>
                <input
                  type="radio"
                  name="tools"
                  id="select"
                  onClick={() =>
                    this.setState({ note: 0, buttonBoolSMD: false, handle: 0 })
                  }
                  defaultChecked="checked"
                />
                Select
              </div>
              <div className="box-devider">
                Notes
                <input
                  type="radio"
                  name="tools"
                  id="wholeNote"
                  onClick={() =>
                    this.setState({
                      note: 1,
                      buttonBoolSMD: true,
                      handle: 1,
                      rest: false,
                    })
                  }
                />
                Whole
                <input
                  type="radio"
                  name="tools"
                  id="halfNote"
                  onClick={() =>
                    this.setState({
                      note: 2,
                      buttonBoolSMD: true,
                      handle: 1,
                      rest: false,
                    })
                  }
                />
                Half
                <input
                  type="radio"
                  name="tools"
                  id="quarterNote"
                  onClick={() =>
                    this.setState({
                      note: 4,
                      buttonBoolSMD: true,
                      handle: 1,
                      rest: false,
                    })
                  }
                />
                Quarter
                <input
                  type="radio"
                  name="tools"
                  id="eightNote"
                  onClick={() =>
                    this.setState({
                      note: 8,
                      buttonBoolSMD: true,
                      handle: 1,
                      rest: false,
                    })
                  }
                />
                eight
              </div>
              <div className="box-devider">
                Rests
                <input
                  type="radio"
                  name="tools"
                  id="wholeNoteRest"
                  onClick={() =>
                    this.setState({
                      note: 1,
                      buttonBoolSMD: true,
                      handle: 1,
                      rest: true,
                    })
                  }
                />
                Whole
                <input
                  type="radio"
                  name="tools"
                  id="halfNoteRest"
                  onClick={() =>
                    this.setState({
                      note: 2,
                      buttonBoolSMD: true,
                      handle: 1,
                      rest: true,
                    })
                  }
                />
                Half
                <input
                  type="radio"
                  name="tools"
                  id="quarterNoteRest"
                  onClick={() =>
                    this.setState({
                      note: 4,
                      buttonBoolSMD: true,
                      handle: 1,
                      rest: true,
                    })
                  }
                />
                Quarter
                <input
                  type="radio"
                  name="tools"
                  id="eightNoteRest"
                  onClick={() =>
                    this.setState({
                      note: 8,
                      buttonBoolSMD: true,
                      handle: 1,
                      rest: true,
                    })
                  }
                />
                eight
              </div>

              <div className="box-devider">
                <input
                  type="radio"
                  name="accidental"
                  id="natural"
                  defaultChecked="checked"
                />
                n
                <input type="radio" name="accidental" id="flat" />b
                <input type="radio" name="accidental" id="sharp" />#
              </div>
              <div className="box-devider">
                <input
                  type="radio"
                  name="tools"
                  id="barLine"
                  onClick={() =>
                    this.setState({ buttonBoolSMD: true, note: 0, handle: 2 })
                  }
                />{" "}
                Bar line
              </div>
            </div>
          </div>

          <div className="container">
            <p className="container-header">Player</p>
            <div className="box-devider1">
              <div className="box-devider">
                <button id="startPauseSwitch" onClick={() => this.Metronome()}>
                  M
                </button>
                <button
                  id="startPauseSwitch"
                  onClick={() => this.startPauseSwitch()}
                >
                  {this.state.playIcon ? (
                    <div className="button-play"></div>
                  ) : null}
                  {this.state.pauseIcon ? (
                    <div className="button-pause"></div>
                  ) : null}
                </button>
                <button id="clockStop" onClick={() => this.clockStop()}>
                  <div className="button-stop"></div>
                </button>
                tempo
                <input
                  type="number"
                  defaultValue="120"
                  min="60"
                  max="200"
                  onChange={this.tempo.bind(this)}
                />
                bpm
              </div>
              <div className="box-devider">
                <input
                  type="range"
                  id="vol"
                  name="vol"
                  min="0"
                  max="100"
                  defaultValue="100"
                  onChange={this.volume.bind(this)}
                ></input>
                {this.state.volume}
              </div>
            </div>
            <div
              className="task"
              onClick={() => {
                if (this.state.workSheet === false) {
                  this.setState({ workSheet: true });
                  return;
                }
                if (this.state.workSheet === true) {
                  this.setState({ workSheet: false });
                  return;
                }
              }}
            >
              task: {this.state.completed}/{this.state.task.length}
            </div>
          </div>

          {/* ----------------------------------- Measurments ----------------------------------- */}
          {this.state.viewOption ? (
            <div className="container">
              <div className="container-popUp">
                <p className="container-header">Measurments</p>
                <div>
                  Clef
                  <button onClick={() => this.setState({ clef: "ð„ž" })}>
                    G clef
                  </button>
                  <button onClick={() => this.setState({ clef: "ð„¢" })}>
                    F Clef
                  </button>
                </div>
                <div>
                  Time Signature
                  <button
                    onClick={() => this.setState({ up: 4, down: 4, handle: 0 })}
                  >
                    4/4
                  </button>
                  <button
                    onClick={() => this.setState({ up: 3, down: 4, handle: 0 })}
                  >
                    3/4
                  </button>
                  <button
                    onClick={() => this.setState({ up: 2, down: 4, handle: 0 })}
                  >
                    2/4
                  </button>
                  Custom Up
                  <input
                    type="number"
                    defaultValue="4"
                    min="1"
                    max="20"
                    onChange={this.keySigUp.bind(this)}
                  />
                  Down
                  <select onChange={this.keySigDown}>
                    <option value="1">1</option>
                    <option value="2">2</option>
                    <option value="4">4</option>
                    <option value="8">8</option>
                    <option value="16">16</option>
                  </select>
                </div>
                <div>
                  <button
                    onClick={() => {
                      this.setStarting();
                    }}
                  >
                    Apply
                  </button>

                  <button onClick={() => this.removeStartingMeasure()}>
                    Delete Starting Measure
                  </button>
                  <button
                    className="buttonUpDown"
                    onClick={() => this.viewOptionOff()}
