import React, { useEffect } from "react";

import { useSelector, useDispatch } from "react-redux";
import { getUserList } from "../features/userSlice";

function StudentList() {
  const dispatch = useDispatch();
  const [student, setStudent] = React.useState("");
  const [veiwStudent, setViewStudent] = React.useState(null);
  const token = useSelector((state) => state.auth.token);
  const users = useSelector((state) => state.user.users);

  useEffect(() => {
    dispatch(getUserList(token));
    // eslint-disable-next-line
  }, []);

  console.log(student);
  console.log(users);
  console.log(veiwStudent);
  return (
    <div className="studentlist">
      <input onChange={(e) => setStudent(e.target.value)} />
      <div className="studentlist__student">
        {users
          .filter((user) => {
            if (student === "") {
              return user;
            } else if (
              user.full_name.toLowerCase().includes(student.toLowerCase())
            ) {
              return user;
            } else {
              return null;
            }
          })
          .map((u, index) => (
            <div onClick={() => setViewStudent(u.id)} className="" key={index}>
              <div className="">
                <p>{`${u.first_name} ${u.last_name}`}</p>
              </div>
            </div>
          ))}
      </div>
    </div>
  );
}

export default StudentList;
