import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getAnnouncementList } from "../features/announcementSlice";
import Announcement from "./Announcement";
import Pagination from "@material-ui/lab/Pagination";

const paging = (page - 1) * 5;

function AnnouncementList() {
  const dispatch = useDispatch();
  const { token, userId } = useSelector((state) => state.auth);
  const announcements = useSelector(
    (state) => state.announcement.announcements
  );
  const count = announcements.length / 5;
  const [page, setPage] = useState(1);

  useEffect(() => {
    dispatch(getAnnouncementList(token));
  }, []);
  console.log(announcements);

  const handleChangePage = (event, value) => {
    setPage(value);
  };
  console.log(page);

  //   const pagedAnnouncements = announcements.slice((page - 1) * 5, 5);
  //   console.log(pagedAnnouncements);
  return (
    <>
      {announcements &&
        announcements.slice(paging, 5).map((item) => {
          return (
            <Announcement
              title={item.name}
              content={item.content}
              avatar={item.avatar}
            />
          );
        })}
      <div className="announcement__footer">
        <Pagination
          count={count}
          siblingCount={0}
          color="secondary"
          onChange={handleChangePage}
        />
      </div>
    </>
  );
}

export default AnnouncementList;
