import React, { useState } from "react";
import Button from "@material-ui/core/Button";
import { useSelector } from "react-redux";
import { Typography } from "@material-ui/core";

const lessonTitle = [
  {
    id: 1,
    title: "Lesson #1",
  },
  {
    title: "Lesson #2",
  },
];

const lessonSteps = [
  {
    stepNumber: 1,
    stepName: "First",
    stepDescription: "First Sentence",
    stepTitle: 1,
    image1: "",
    image2: "",
  },
  {
    stepNumber: 2,
    stepName: "Second",
    stepDescription: "Second Sentence description",
    stepTitle: 1,
    image1: "",
    image2: "",
  },
];

function LessonCreator() {
  const { is_teacher, token } = useSelector((state) => state.auth);
  const [search, setSearch] = useState("");

  return (
    <div className="lessons">
      <Typography variant="h6">Lesson Creator</Typography>
      {is_teacher && (
        <div className="create-lesson">
          <Button variant="contained" color="primary">
            Add New Lesson
          </Button>
        </div>
      )}
      <Typography variant="h6">Lessons</Typography>
      <div className="lesson-list">
        {lessonTitle.map((lesson) => (
          <div className="lesson-name">
            <Typography variant="subtitle2">{lesson.title}</Typography>
          </div>
        ))}
      </div>
    </div>
  );
}

export default LessonCreator;
