import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { createAnnouncement } from "../features/announcementSlice";
import TextField from "@material-ui/core/TextField";

function CreateAnnouncement() {
  const token = useSelector((state) => state.auth.token);
  const [name, setName] = React.useState("");
  const [content, setContent] = React.useState("");
  const dispatch = useDispatch();

  const handlePost = () => {
    const formData = new FormData();
    formData.append("name", name);
    formData.append("content", content);
    formData.append("isGlobal", true);
    formData.append("classroom_id", 2);
    formData.append("author_id", 13);
    dispatch(createAnnouncement(token, formData));
  };

  return (
    <div className="announcement__create">
      <TextField
        id="standard-multiline-flexible"
        label="Title"
        multiline
        rowsMax={4}
        value={name}
        onChange={(e) => setName(e.target.value)}
      />
      <TextField
        id="standard-multiline-flexible"
        label="Content"
        multiline
        rowsMax={4}
        value={name}
        onChange={(e) => setContent(e.target.value)}
      />

      <button onClick={handlePost}>Post</button>
    </div>
  );
}

export default CreateAnnouncement;
