import { createSlice } from "@reduxjs/toolkit";

export const metaSlice = createSlice({
  name: "meta",
  initialState: {
    showSidebar: true,
  },
  reducers: {
    toggle: (state) => {
      state.showSidebar = false;
    },
  },
});

export const { toggle } = metaSlice.actions;
//actions under here
export default metaSlice.reducer;
