import React, { useState, useEffect } from "react";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import { useSelector, useDispatch } from "react-redux";

import { toggle } from "../features/updateSlice";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import Select from "@material-ui/core/Select";

import {
  getSchoolYear,
  getGradeLevel,
  getCover,
} from "../features/schoolSlice";

export default function CreateClassroom() {
  const [open, setOpen] = React.useState(false);

  const [age, setAge] = React.useState("");
  const dispatch = useDispatch();
  const [cover, setCover] = useState("");
  const [year, setYear] = useState("");
  const [gradeLevel, setGradeLevel] = useState("");
  const schoolYear = useSelector((state) => state.school.schoolYear);
  const covers = useSelector((state) => state.school.cover);
  const level = useSelector((state) => state.school.gradeLevel);
  const { token, first_name, last_name } = useSelector((state) => state.auth);

  useEffect(() => {
    dispatch(getSchoolYear(token));
    dispatch(getGradeLevel(token));
    dispatch(getCover(token));
  }, [token]);

  const handleLevelChange = (e) => {
    console.log(e.target.value);
    setGradeLevel(e.target.value);
  };
  const handleYearChange = (e) => {
    console.log(e.target.value);
    setYear(e.target.value);
  };

  const handleCoverChange = (e) => {
    setCover(e.target.value);
  };
  const handleChange = (event) => {
    setAge(event.target.value);
  };

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div>
      <Button variant="outlined" color="primary" onClick={handleClickOpen}>
        Create new Classroom
      </Button>
      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="form-dialog-title"
      >
        <DialogTitle id="form-dialog-title">New Classroom</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Add announcements globally or for specific Classroom.
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            id="name"
            label="Classroom Name"
            type="text"
            fullWidth
            onChange={handleName}
          />
          <TextField
            autoFocus
            margin="dense"
            id="schedule"
            label="Schedule"
            type="text"
            onChange={handleContent}
            fullWidth
          />
          <InputLabel id="demo-simple-select-label">Age</InputLabel>
          <Select
            labelId="demo-simple-select-label"
            id="demo-simple-select"
            value={age}
            onChange={handleChange}
          >
            {covers &&
              covers.map((item) => {
                return (
                  <MenuItem key={item.id} value={item.id}>
                    {item.name}
                  </MenuItem>
                );
              })}
          </Select>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Cancel
          </Button>
          <Button onClick={handlePost} color="primary">
            Post
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
