import { Avatar, Typography } from "@material-ui/core";
import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getBoardMessages } from "../features/boardSlice";

function MessageBoard(props) {
  const dispatch = useDispatch();
  const { boardMessages, error } = useSelector((state) => state.board);
  const { token, userId } = useSelector((state) => state.auth);
  const [seconds, setSeconds] = React.useState(0);

  console.log(boardMessages);
  console.log(error);

  useEffect(() => {
    dispatch(getBoardMessages(token, props.id));
    const interval = setInterval(() => {
      setSeconds((seconds) => seconds + 1);
      dispatch(getBoardMessages(token, props.id));
    }, 10000);

    return () => clearInterval(interval);
  }, [token, userId, dispatch]);

  return (
    <div className="messageBoard">
      <div className="messageBoard__header">
        <Typography variant="subtitle2">
          {boardMessages.classroom_name} Message Board {seconds}
        </Typography>
      </div>
      <div className="messageBoard__body">
        <div className="messageBoard__message">
          <Avatar src="">J</Avatar>
        </div>
      </div>
      <div className="messageBoard__send"></div>
    </div>
  );
}

export default MessageBoard;
