import {
  Button,
  FormControl,
  InputLabel,
  MenuItem,
  TextField,
  Typography,
} from "@material-ui/core";
import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getQuizDetail } from "../../features/quizSlice";
import { makeStyles, useTheme } from "@material-ui/core/styles";
import Select from "@material-ui/core/Select";
import Input from "@material-ui/core/Input";
import Chip from "@material-ui/core/Chip";
import { getChoiceList } from "../../features/quizSlice";

const useStyles = makeStyles((theme) => ({
  textField: {
    minWidth: "100%",
    margin: "10px 0",
  },
  buttonStyle: {
    minWidth: "100%",
    margin: "10px 0",
    height: "3rem",
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
    maxWidth: 300,
  },
  chips: {
    display: "flex",
    flexWrap: "wrap",
  },
  chip: {
    margin: 2,
  },
}));

function getStyles(name, choices, theme) {
  return {
    fontWeight:
      choices.indexOf(name) === -1
        ? theme.typography.fontWeightRegular
        : theme.typography.fontWeightMedium,
  };
}
const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: 250,
    },
  },
};

function QuizDetail(props) {
  const classes = useStyles();
  const theme = useTheme();
  const dispatch = useDispatch();
  const [count, setCount] = useState(1);
  const [questions, setQuestons] = useState([]);
  const [choices, setChoices] = useState([]);
  const quizDetail = useSelector((state) => state.quiz.quizDetail);
  const token = useSelector((state) => state.auth.token);
  const choiceList = useSelector((state) => state.quiz.choiceList);

  useEffect(() => {
    dispatch(getQuizDetail(token, props.id));
  }, [dispatch, token, props.id]);

  const handleChange = (event, i) => {
    setChoices(choices);
  };

  console.log(choices);
  useEffect(() => {
    dispatch(getChoiceList(token));
  }, [token, dispatch]);
  const renderInputs = (value) => {
    const inputs = [];
    for (let i = 0; i < value; i++) {
      inputs.push(
        <div className="createQuiz__question">
          <Typography>Question Form</Typography>
          <TextField
            id={`outlined-basic-${i}`}
            label="Quiz Title"
            variant="outlined"
            className={classes.textField}
            size="small"
            multiline
          />
          <FormControl className={classes.formControl}>
            <InputLabel id={`chip-label-${i}`}>Chip</InputLabel>
            <Select
              labelId={`chip-label-${i}`}
              id={`chip-${i}`}
              multiple
              value={choices}
              onChange={(e) => handleChange(e, i)}
              input={<Input id={`chip-${i}`} />}
              renderValue={(selected) => (
                <div className={classes.chips}>
                  {selected.map((value) => (
                    <Chip
                      key={value}
                      label={value.name}
                      className={classes.chip}
                    />
                  ))}
                </div>
              )}
              MenuProps={MenuProps}
            >
              {choiceList.map((name) => (
                <MenuItem
                  key={name.id}
                  value={name}
                  style={getStyles(name, choices, theme)}
                >
                  {name.name}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        </div>
      );
    }
    return inputs;
  };
  console.log(count);
  const increment = () => {
    setCount(count + 1);
  };

  return (
    <div>
      {quizDetail.name}
      <div className="createQuiz">
        <Typography variant="h6">Add Questions</Typography>
        {renderInputs(count)}
        <Button variant="contained" color="primary" onClick={increment}>
          Add
        </Button>
      </div>
    </div>
  );
}

export default QuizDetail;
