import React, { useEffect } from "react";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import { makeStyles } from "@material-ui/core/styles";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import { getGradeLevel } from "../features/schoolSlice";
import { useSelector, useDispatch } from "react-redux";
import TextField from "@material-ui/core/TextField";

const useStyles = makeStyles((theme) => ({
  formControl: {
    minWidth: "50%",
    margin: "10px 0",
  },
  textField: {
    minWidth: "100%",
    margin: "10px 0",
  },
}));
function CreateQuiz() {
  const classes = useStyles();
  const dispatch = useDispatch();
  const { userId, token } = useSelector((state) => state.auth);
  const [level, setLevel] = React.useState("");
  const levels = useSelector((state) => state.school.gradeLevel);

  const handleChange = (event) => {
    setLevel(event.target.value);
  };

  useEffect(() => {
    dispatch(getGradeLevel(token));
  }, [token, dispatch]);

  return (
    <div className="createQuiz">
      <TextField
        id="outlined-basic"
        label="Quiz Title"
        variant="outlined"
        className={classes.textField}
      />
      <TextField
        id="outlined-basic"
        label="Description"
        variant="outlined"
        className={classes.textField}
      />
      <div className="createQuiz__level">
        <FormControl variant="outlined" className={classes.formControl}>
          <InputLabel id="select-outlined-label">Grade Level</InputLabel>
          <Select
            color="primary"
            labelId="select-outlined-label"
            id="select-outlined"
            value={level}
            onChange={handleChange}
            label="Grade Level"
          >
            {levels?.map((item) => {
              return <MenuItem value={item.id}>{item.name}</MenuItem>;
            })}
          </Select>
        </FormControl>
      </div>
    </div>
  );
}

export default CreateQuiz;
