import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { createEnrollment } from "../features/enrollmentSlice";
import { getUserList } from "../features/userSlice";
function EnrollStudent(props) {
  const dispatch = useDispatch();
  const { token } = useSelector((state) => state.auth);
  const { users } = useSelector((state) => state.user);

  useEffect(() => {
    dispatch(getUserList(token));
  }, [token, dispatch]);
  console.log(props.selectedStudent);
  const submitEnroll = () => {
    const formData = new FormData();
    formData.append("student_id", "student");
    formData.append("classroom_id", props.id);
    dispatch(createEnrollment(token, formData));
  };

  return (
    <div className="enroll">
      <div className="enroll__card">
        <img src="http://localhost:8000/media/avatar/94941016_1573207659517515_4255945916093038592_o_D26a87L.jpg"></img>
        <div className="enroll__details">
          <p>Austin Egaran</p>
          <p>bio</p>
          <p>email</p>
        </div>
        <button onClick={submitEnroll}>Enroll Student to this classroom</button>
      </div>
    </div>
  );
}

export default EnrollStudent;
