import React, { useEffect } from "react";
import { getQuizzes } from "../../features/quizSlice";
import { useSelector, useDispatch } from "react-redux";

function ClassroomQuizList(props) {
  const dispatch = useDispatch();
  const token = useSelector((state) => state.auth.token);
  const quizzes = useSelector((state) => state.quiz.quizzes);
  const update = useSelector((state) => state.update.isUpdated);

  useEffect(() => {
    dispatch(getQuizzes(token));
  }, [token, update, dispatch]);
  const quizList = quizzes.filter((item) => item.level === props.level);

  console.log(quizzes);
  return (
    <div className="quizlist">
      <h3>Quiz List</h3>
      {quizList?.map((item) => {
        return (
          <div className="quizlist__item">
            <div className="quizlist__itemName">{item.name}</div>
            <div className="quizlist__itemLevel">{item.level_name}</div>
            <button>Take Quiz</button>
          </div>
        );
      })}
    </div>
  );
}

export default ClassroomQuizList;
