import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { makeStyles } from "@material-ui/core/styles";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import Divider from "@material-ui/core/Divider";
import ListItemText from "@material-ui/core/ListItemText";
import ListItemAvatar from "@material-ui/core/ListItemAvatar";
import Avatar from "@material-ui/core/Avatar";
import Typography from "@material-ui/core/Typography";
import { pink } from "@material-ui/core/colors";
import { getAnnouncementList } from "../features/mainSlice";
import Paper from "@material-ui/core/Paper";
import Pagination from "@material-ui/lab/Pagination";

const useStyles = makeStyles((theme) => ({
  root: {
    width: "100%",

    backgroundColor: theme.palette.background.paper,
  },
  inline: {
    display: "inline",
  },
  pink: {
    color: theme.palette.getContrastText(pink[500]),
    backgroundColor: pink[500],
  },
  paper: {
    marginTop: 0,
    overflowY: "scroll",
  },
}));

export default function Announcements() {
  const classes = useStyles();
  const dispatch = useDispatch();
  const token = useSelector((state) => state.auth.token);
  const announcements = useSelector((state) => state.main.announcements);
  const sortedAnnouncements = announcements.sort((a, b) => b.date - a.date);
  const [page, setPage] = useState(1);
  const pages = sortedAnnouncements.slice();

  useEffect(() => {
    dispatch(getAnnouncementList(token));
  }, []);

  handlePage = (e, page) => {
    setPage(page);
  };
  return (
    <Paper className={classes.paper}>
      <List className={classes.root}>
        {announcements.map((d, index) => {
          return (
            <div key={index}>
              <ListItem alignItems="flex-start" button>
                <ListItemAvatar>
                  <Avatar
                    alt="Travis Howard"
                    src={d.avatar}
                    className={classes.pink}
                  ></Avatar>
                </ListItemAvatar>
                <ListItemText
                  primary={d.name}
                  secondary={
                    <React.Fragment>
                      <Typography
                        component="span"
                        variant="body2"
                        className={classes.inline}
                        color="textPrimary"
                      >
                        <small>{d.classroom} : </small>
                      </Typography>
                      {d.content}
                    </React.Fragment>
                  }
                />
              </ListItem>
            </div>
          );
        })}
      </List>
      <Pagination
        onChange={handlePage}
        defaultPage={1}
        page={page}
        count={4}
        size="large"
      />
    </Paper>
  );
}
