import {
  Button,
  FormControl,
  InputLabel,
  TextField,
  Typography,
} from "@material-ui/core";
import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getQuizDetail } from "../../features/quizSlice";
import { makeStyles, useTheme } from "@material-ui/core/styles";
import Select from "@material-ui/core/Select";

import Chip from "@material-ui/core/Chip";

const useStyles = makeStyles((theme) => ({
  textField: {
    minWidth: "100%",
    margin: "10px 0",
  },
  buttonStyle: {
    minWidth: "100%",
    margin: "10px 0",
    height: "3rem",
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
    maxWidth: 300,
  },
  chips: {
    display: "flex",
    flexWrap: "wrap",
  },
  chip: {
    margin: 2,
  },
}));

function getStyles(name, personName, theme) {
  return {
    fontWeight:
      personName.indexOf(name) === -1
        ? theme.typography.fontWeightRegular
        : theme.typography.fontWeightMedium,
  };
}

function QuizDetail(props) {
  const classes = useStyles();
  const dispatch = useDispatch();
  const [count, setCount] = useState(1);
  const quizDetail = useSelector((state) => state.quiz.quizDetail);
  const token = useSelector((state) => state.auth.token);

  useEffect(() => {
    dispatch(getQuizDetail(token, props.id));
  }, [dispatch, token, props.id]);

  const handleChangeMultiple = (event) => {
    const { options } = event.target;
    const value = [];
    for (let i = 0, l = options.length; i < l; i += 1) {
      if (options[i].selected) {
        value.push(options[i].value);
      }
    }
    setPersonName(value);
  };
  const renderInputs = (value) => {
    const inputs = [];
    for (let i = 0; i < value; i++) {
      inputs.push(
        <div className="createQuiz__question">
          <Typography>Question Form</Typography>
          <TextField
            id={`outlined-basic-${i}`}
            label="Quiz Title"
            variant="outlined"
            className={classes.textField}
            size="small"
            multiline
          />
          <FormControl className={classes.formControl}>
            <InputLabel id="demo-mutiple-chip-label">Chip</InputLabel>
            <Select
              labelId="demo-mutiple-chip-label"
              id="demo-mutiple-chip"
              multiple
              value={personName}
              input={<Input id="select-multiple-chip" />}
              renderValue={(selected) => (
                <div className={classes.chips}>
                  {selected.map((value) => (
                    <Chip key={value} label={value} className={classes.chip} />
                  ))}
                </div>
              )}
              MenuProps={MenuProps}
            >
              {names.map((name) => (
                <MenuItem
                  key={name}
                  value={name}
                  style={getStyles(name, personName, theme)}
                >
                  {name}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        </div>
      );
    }
    return inputs;
  };
  console.log(count);
  const increment = () => {
    setCount(count + 1);
  };

  return (
    <div>
      {quizDetail.name}
      <div className="createQuiz">
        <Typography variant="h6">Add Questions</Typography>
        {renderInputs(count)}
        <Button onClick={increment}>Add</Button>
      </div>
    </div>
  );
}

export default QuizDetail;
