import { createSlice } from "@reduxjs/toolkit";
import axios from "axios";

export const classroomSlice = createSlice({
  name: "classroom",
  initialState: {
    classroom: {},
    classrooms: [],
    error: null,
    loading: false,
  },
  reducers: {
    getClassroomStart: (state) => {
      state.loading = true;
    },
    getClassroomSuccess: (state, action) => {
      state.classroom = action.payload;
      state.loading = false;
    },
    getClassroomFail: (state, action) => {
      state.error = action.payload;
      state.loading = false;
    },
    getClassroomList: (state) => {
      state.loading = true;
    },
    getClassroomList: (state, action) => {
      state.classrooms = actions.payload;
    },
  },
});

export const {
  getClassroomStart,
  getClassroomSuccess,
  getClassroomFail,
} = classroomSlice.actions;

export const getClassroom = (token, id) => {
  return (dispatch) => {
    dispatch(getClassroomStart());
    console.log("getClassroom ====>");
    axios.defaults.headers = {
      "Content-Type": "application/json",
      Authorization: `Token ${token}`,
    };
    axios
      .get(`http://127.0.0.1:8000/api/classrooms/${id}`)
      .then((res) => {
        console.log("getting classrooms success");
        console.log(res.data);
        const classrooms = res.data.filter(
          (classroom) => classroom.teacher_id === id
        );
        dispatch(getClassroomSuccess(classrooms));
      })
      .catch((err) => {
        dispatch(getClassroomFail(err.message));
      });
  };
};

export default classroomSlice.reducer;
