import React from "react";
import Sidebar from "../components/Sidebar";
import { useSelector, useDispatch } from "react-redux";
import useWindowDimensions from "../hooks/window-dimensions";
import { toggle } from "../features/metaSlice";

import Header from "../components/Header";

function Layout(props) {
  const showSidebar = useSelector((state) => state.meta.showSidebar);
  const dispatch = useDispatch();
  const { width } = useWindowDimensions();

  if (width < 1024) {
    dispatch(toggle(!showSidebar));
  }

  return (
    <>
      {props.isAuthenticated ? (
        <div className="app">
          {showSidebar ? <Sidebar /> : null}

          <div className="main">
            <Header />
            <div className="routes">{props.routes}</div>
            {/* <div className="footer">footer text</div> */}
          </div>
        </div>
      ) : (
        <div className="app">{props.routes}</div>
      )}
    </>
  );
}

export default Layout;
