import React from "react";
import styles from "../styles/Sidebar.module.css";
import { useSpring, animated } from "react-spring";

import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";

import InboxIcon from "@material-ui/icons/Inbox";
import DraftsIcon from "@material-ui/icons/Drafts";

const show = { width: 0, from: { width: 300 } };
const hide = { width: 300, from: { width: 0 } };

function Sidebar(props) {
  console.log(props.toggle);
  const [selectedIndex, setSelectedIndex] = React.useState(1);

  const handleListItemClick = (event, index) => {
    setSelectedIndex(index);
  };
  const springProps = useSpring(props.toggle ? hide : show);

  return (
    <animated.div style={springProps} className={styles.sidebar}>
      <div className={styles.menu}>
        <div className={styles.menuItem}>Dashboard</div>
        <div className={styles.menuItem}>Simulator</div>
      </div>
    </animated.div>
  );
  //   return <div className={styles.sidebar}></div>;
}

export default Sidebar;
