import React, { useEffect } from "react";
import { getConvoList } from "../features/convoSlice";
import { useSelector, useDispatch } from "react-redux";

function Inbox() {
  const dispatch = useDispatch();
  const convos = useSelector((state) => state.convo.convos);
  const { token, userId, first_name, last_name } = useSelector(
    (state) => state.auth
  );
  //dummy data
  //

  useEffect(() => {
    dispatch(getConvoList(token, userId));
  }, [userId, token, dispatch]);

  // const convos = [...new Set(messages.map((item) => item.receiver))];
  // const names = [...new Set(messages.map((item) => item.receiver_name))].filter(
  //   (item) => item !== `${first_name} ${last_name}`
  // );
  // const renderConvos = (user1, user2) => {
  //   if()
  // }

  console.log(convos);
  return (
    <div className="inbox">
      <div className="inbox__container">
        <div className="inbox__senders">
          {convos.map((item) =>
            userId === item.user1 ? (
              <Typography>{item.user2_name}</Typography>
            ) : (
              <Typography>{item.user1_name}</Typography>
            )
          )}
        </div>
        <div className="inbox__content">
          <div className="inbox__head"></div>
          <div className="inbox__body"></div>
          <div className="inbox__newMessage"></div>
        </div>
      </div>
    </div>
  );
}

export default Inbox;
