import React, { useEffect } from "react";

import Header from "./Header";
// import { authLogin } from "./features/userSlice";
import { useDispatch, useSelector } from "react-redux";
import { authCheckState, getUserList } from "./features/userSlice";
import { getMenuList } from "./features/mainSlice";

function App() {
  const dispatch = useDispatch();
  const user = useSelector((state) => state.user);
  const menuList = useSelector((state) => state.main.menu);
  // const loadUserList = () => {
  //   dispatch(getUserList(user.token));
  // };

  useEffect(() => {
    dispatch(authCheckState());
    dispatch(getUserList(user.token));
    dispatch(getMenuList(user.token));
  }, [user.token, dispatch]);
  const userList = useSelector((state) => state.user.userList);
  console.log(menuList);
  console.log(user);
  console.log(user.token);
  console.log(userList);
  const onLogin = () => {
    // const username = "aceDriz";
    // const password = "r41v3x21";
    // dispatch(authLogin(username, password));
    dispatch(getUserList(user.token));
  };
  return (
    <div className="app">
      <Header onLogin={onLogin} />
      {user.username}
      <br />
      {user.first_name}
      <br />
      {menuList.map((menu, index) => (
        <span key={index}>{menu.title}</span>
      ))}
    </div>
  );
}

export default App;
