import React from "react";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import { useSelector, useDispatch } from "react-redux";
import SendIcon from "@material-ui/icons/Send";
import {
  createAnnouncement,
  getAnnouncementList,
} from "../features/announcementSlice";
import {
  FormControl,
  IconButton,
  InputLabel,
  MenuItem,
  Select,
} from "@material-ui/core";

export default function CreateMessage() {
  const [open, setOpen] = React.useState(false);
  const { token, userId } = useSelector((state) => state.auth);
  const userList = useSelector((state) => state.user.users);

  const [receiver, setReceiver] = React.useState("");
  const [content, setContent] = React.useState("");
  const dispatch = useDispatch();

  const handlePost = () => {
    const formData = new FormData();

    dispatch(createAnnouncement(token, formData));
    dispatch(getAnnouncementList(token));

    setOpen(false);
  };

  const handleContent = (e) => {
    setContent(e.target.value);
  };

  const handleChangeReceiver = (e) => {
    setName(e.target.value);
  };
  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <>
      <IconButton onClick={handleClickOpen}>
        <SendIcon />
      </IconButton>
      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="form-dialog-title"
      >
        <DialogTitle id="form-dialog-title">Messaging</DialogTitle>
        <DialogContent>
          <DialogContentText>Send a Message</DialogContentText>
          <FormControl variant="outlined">
            <InputLabel id="select-outlined-label">Grade Level</InputLabel>
            <Select
              color="primary"
              labelId="select-outlined-label"
              id="select-outlined"
              value={userList}
              onChange={handleChangeReceiver}
              label="Grade Level"
            >
              {userList?.map((user) => {
                return (
                  <MenuItem key={user.id} value={user.id}>
                    {user.full_name}
                  </MenuItem>
                );
              })}
            </Select>
          </FormControl>
          <TextField
            margin="dense"
            id="content"
            label="Content"
            type="text"
            onChange={handleContent}
            fullWidth
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Cancel
          </Button>
          <Button onClick={handlePost} color="primary">
            Send
          </Button>
        </DialogActions>
      </Dialog>
    </>
  );
}
