import React, { useState } from "react";
import EnrollmentsList from "../components/EnrollmentsList";
import { useSelector } from "react-redux";

import ClassroomList from "../components/ClassroomList.js";
import StudentList from "../components/StudentList";
import AnnouncementList from "../components/AnnouncementList";
import CreateClassroom from "../components/CreateClassroom";
import CreateAnnouncement from "../components/CreateAnnouncement";
import EditClassroom from "../components/EditClassroom";

function Dashboard() {
  const [showCreateClassroom, setShowCreateClassroom] = useState(false);
  const [showCreateAnnouncement, setShowCreateAnnouncement] = useState(false);
  const [showEditClassroom, setShowEditClassroom] = useState(false);

  const is_teacher = useSelector((state) => state.auth.is_teacher);

  const handleShowCreate = () => {
    setShowCreateClassroom(!showCreateClassroom);
    setShowCreateAnnouncement(false);
  };

  const handleShowAnnouncement = () => {
    setShowCreateAnnouncement(!showCreateAnnouncement);
    setShowCreateClassroom(false);
  };
  return (
    <div className="dashboard">
      <div className="dashboard__main">
        <div className="dashboard__head">
          <div className="head__buttons">
            <CreateClassroom />
            <button onClick={handleShowAnnouncement}>
              Create new Announcement
            </button>
            <button>Edit classroom</button>
            <EditClassroom />
          </div>

          {showCreateAnnouncement ? <CreateAnnouncement /> : null}
        </div>
        <div className="dashboard__classrooms">
          {is_teacher ? <ClassroomList /> : <EnrollmentsList />}
        </div>
        <div className="dashboard__students">
          <div className="dashboard__students-search">
            <StudentList />
          </div>

          <div className="dashboard__students-profile">profile</div>
        </div>
      </div>

      <div className="dashboard__announcement">
        <div className="announcement__header">Announcements</div>

        <AnnouncementList />
      </div>
    </div>
  );
}

export default Dashboard;
