          <Header toggle={toggleDrawer} isAuth={isAuthenticated} />

          <div className={styles.main}>
            {props.isAuthenticated ? <Sidebar toggle={drawer} /> : null}
            <div className={styles.content}>{routes}</div>
          </div>
          <div className={styles.footer}>Musimu</div>
        </div>
      </div>
    </>
  );
}

