import React, { useEffect } from "react";
import { getConvoList } from "../features/convoSlice";
import { useSelector, useDispatch } from "react-redux";
import Typography from "@material-ui/core/Typography";

function Inbox() {
  const dispatch = useDispatch();
  const convos = useSelector((state) => state.convo.convos);
  const { token, userId, first_name, last_name } = useSelector(
    (state) => state.auth
  );
  const [currentConvo, setCurrentConvo] = React.useState(null);
  //dummy data
  //

  useEffect(() => {
    dispatch(getConvoList(token, userId));
  }, [userId, token, dispatch]);

  // const convos = [...new Set(messages.map((item) => item.receiver))];
  // const names = [...new Set(messages.map((item) => item.receiver_name))].filter(
  //   (item) => item !== `${first_name} ${last_name}`
  // );
  // const renderConvos = (user1, user2) => {
  //   if()
  // }

  const openConvo = (convo) => {
    console.log(convo);
    setCurrentConvo(convo);
  };

  console.log(convos);
  return (
    <div className="inbox">
      <div className="inbox__container">
        <div className="inbox__senders">
          {convos?.map((item) =>
            userId === item.user1 ? (
              <div key={item.id} onClick={() => openConvo(item)}>
                <Typography>{item.user2_name}</Typography>
              </div>
            ) : (
              <div key={item.id} onClick={() => openConvo(item)}>
                <Typography>{item.user1_name}</Typography>
              </div>
            )
          )}
        </div>
        <div className="inbox__content">
          <div className="inbox__head">
            {userId === currentConvo?.user1
              ? currentConvo.user2_name
              : currentConvo.user1_name}
          </div>
          <div className="inbox__body">
            {currentConvo.messages.map((item) => (
              <div></div>
            ))}
          </div>
          <div className="inbox__newMessage"></div>
        </div>
      </div>
    </div>
  );
}

export default Inbox;
