import React, { useEffect } from "react";
import ListItemAvatar from "@material-ui/core/ListItemAvatar";
import { Avatar, CircularProgress } from "@material-ui/core";
import FolderIcon from "@material-ui/icons/Folder";
import { getModules } from "../features/moduleSlice";
import { useDispatch, useSelector } from "react-redux";
import SystemUpdateAltIcon from "@material-ui/icons/SystemUpdateAlt";

function Module() {
  const dispatch = useDispatch();
  const token = useSelector((state) => state.auth.token);
  const { modules, loading, error } = useSelector((state) => state.modules);

  useEffect(() => {
    dispatch(getModules(token));
  }, []);

  return (
    <div className="modules">
      {loading ? (
        <div className="loading">
          <CircularProgress color="secondary" />
        </div>
      ) : (
        <>
          {modules?.map((item) => {
            return (
              <div className="module__item">
                <div className="module__name">
                  <ListItemAvatar>
                    <Avatar>
                      <FolderIcon />
                    </Avatar>
                  </ListItemAvatar>
                  <p>{item.name}</p>
                </div>

                <div className="module__link">
                  <SystemUpdateAltIcon />
                  <a href={`${item.module}`}>Download</a>
                </div>
                <p>{item.created}</p>
              </div>
            );
          })}
        </>
      )}
      <div className="module__upload">
        <div className="upload__input">
          <input onChange={this.onInputFile} type="file" name="module"></input>
        </div>
      </div>
    </div>
  );
}

export default Module;
