import { createSlice } from "@reduxjs/toolkit";
import axios from "axios";

export const mainSlice = createSlice({
  name: "main",
  initialState: {
    announcements: [],
  },
  reducers: {
    getAnnouncementListSuccess: (state, action) => {
      state.announcements = action.payload;
    },
  },
});

export const { getAnnouncementListSuccess } = mainSlice.actions;

export const getAnnouncementList = (token, id) => {
  return (dispatch) => {
    axios.defaults.headers = {
      "Content-Type": "application/json",
      Authorization: `Token ${token}`,
    };
    axios
      .get("http://127.0.0.1:8000/api/announcements")
      .then((res) => {
        console.log(res.data);
        // const announcements = res.data.filter((c) => c.id === id);
        dispatch(getAnnouncementListSuccess(announcements));
      })
      .catch((err) => {
        console.log(err);
      });
  };
};

export default mainSlice.reducer;
