import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { makeStyles, StylesProvider } from "@material-ui/core/styles";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import styles from "../styles/Announcement.module.css";
import ListItemText from "@material-ui/core/ListItemText";
import ListItemAvatar from "@material-ui/core/ListItemAvatar";
import Avatar from "@material-ui/core/Avatar";
import Typography from "@material-ui/core/Typography";
import { pink } from "@material-ui/core/colors";
import { getAnnouncementList } from "../features/mainSlice";
import Paper from "@material-ui/core/Paper";
import Pagination from "@material-ui/lab/Pagination";

const useStyles = makeStyles((theme) => ({
  root: {
    width: "100%",
    flex: 1,
  },
  inline: {
    display: "inline",
  },
  pink: {
    color: theme.palette.getContrastText(pink[500]),
    backgroundColor: pink[500],
  },
  paper: {
    width: 500,
    height: 620,
    marginTop: 0,
    display: "flex",
    flexDirection: "column",
  },
  pagination: {},
}));

const items = 7;

export default function Announcements() {
  const classes = useStyles();
  const dispatch = useDispatch();
  const token = useSelector((state) => state.auth.token);
  const announcements = useSelector((state) => state.main.announcements);
  // const sortedAnnouncements = announcements.sort(
  //   (a, b) => b.date_posted - a.date_posted
  // );
  const [page, setPage] = useState(1);
  const total = Math.ceil(announcements.length / items);
  const pages = announcements.slice(
    (page - 1) * items,
    (page - 1) * items + items
  );

  useEffect(() => {
    dispatch(getAnnouncementList(token));
  }, []);

  const handlePage = (e, page) => {
    console.log(page);
    setPage(page);
  };
  return (
    <div className={styles.paper}>
      <h3>Announcement</h3>
      <List className={classes.root}>
        {pages.map((d, index) => {
          return (
            <div key={index}>
              <ListItem alignItems="flex-start" button>
                <ListItemAvatar>
                  <Avatar
                    alt="Travis Howard"
                    src={d.avatar}
                    className={classes.pink}
                  ></Avatar>
                </ListItemAvatar>
                <ListItemText
                  primary={d.name}
                  secondary={
                    <React.Fragment>
                      <Typography
                        component="span"
                        variant="body2"
                        className={classes.inline}
                        color="textPrimary"
                      >
                        <small>{d.classroom} : </small>
                      </Typography>
                      {d.content}
                    </React.Fragment>
                  }
                />
              </ListItem>
            </div>
          );
        })}
      </List>
      <Pagination
        className={classes.pagination}
        onChange={handlePage}
        defaultPage={1}
        page={page}
        count={total}
        size="large"
      />
    </div>
  );
}
