import { createSlice } from "@reduxjs/toolkit";
import axios from "axios";

export const classroomSlice = createSlice({
  name: "classroom",
  initialState: {
    classrooms: {},

    error: null,
    loading: false,
  },
  reducers: {
    getClassroomStart: (state) => {
      state.loading = true;
    },
    getClassroomSuccess: (state, action) => {
      state.classrooms = action.payload;
      state.loading = false;
    },
    getClassroomFail: (state, action) => {
      state.error = action.payload;
      state.loading = false;
    },
  },
});

export const {
  getClassroomStart,
  getClassroomSuccess,
  getClassroomFail,
} = classroomSlice.actions;

export const getClassroom = (token, id) => {
  return (dispatch) => {
    dispatch(getClassroomStart());
    axios
      .get(`http://127.0.0.1:8000/api/classrooms/${id}`)
      .then((res) => {
        dispatch(getClassroomSuccess(res.data));
      })
      .catch((err) => {
        dispatch(getClassroomFail(err));
      });
  };
};

export default classroomSlice.reducer;
