import { createSlice } from "@reduxjs/toolkit";
import axios from "axios";

export const userSlice = createSlice({
  name: "user",
  initialState: {
    username: null,
    token: null,
    first_name: null,
    last_name: null,
    userId: null,
    error: null,
    loading: false,
    userList: null,
    isAuthenticated: state.token !== null,
  },
  reducers: {
    authSuccess: (state, action) => {
      state.username = action.payload.username;
      state.token = action.payload.token;
      state.last_name = action.payload.last_name;
      state.first_name = action.payload.first_name;
      state.userId = action.payload.userId;
      state.expirationDate = action.payload.expirationDate.toString();
    },

    logout: (state) => {
      localStorage.removeItem("user");
      state.username = null;
      state.token = null;
      state.first_name = null;
      state.last_name = null;
      state.userId = null;
      state.expirationDate = null;
    },
    getUserListSuccess: (state, action) => {
      state.userList = action.payload;
    },
  },
});

export const { authSuccess, logout, getUserListSuccess } = userSlice.actions;

//if need dispatch; put here
export const authLogin = (username, password) => {
  return (dispatch) => {
    axios
      .post("http://127.0.0.1:8000/rest-auth/login/", {
        username: username,
        password: password,
      })
      .then((res) => {
        console.log(res.data);
        const user = {
          token: res.data.key,
          username,
          last_name: res.data.user_detail.last_name,
          first_name: res.data.user_detail.first_name,
          userId: res.data.user_detail.userId,
          expirationDate: new Date(new Date().getTime() + 86400 * 1000),
        };
        console.log(user);
        localStorage.setItem("user", JSON.stringify(user));
        dispatch(authSuccess(user));
        dispatch(checkAuthTimeout(86400));
      })
      .catch((err) => {
        console.log(err);
      });
  };
};

export const checkAuthTimeout = (expirationDate) => {
  return (dispatch) => {
    setTimeout(() => {
      dispatch(logout());
    }, expirationDate * 1000);
  };
};

export const authCheckState = () => {
  return (dispatch) => {
    const user = JSON.parse(localStorage.getItem("user"));
    if (user === undefined || user === null) {
      dispatch(logout());
    } else {
      const expirationDate = new Date(user.expirationDate);
      if (expirationDate <= new Date()) {
        dispatch(logout());
      } else {
        dispatch(authSuccess(user));
        dispatch(
          checkAuthTimeout(
            (expirationDate.getTime() - new Date().getTime()) / 1000
          )
        );
      }
    }
  };
};

export const getUserList = (token) => {
  return (dispatch) => {
    axios.defaults.headers = {
      "Content-Type": "application/json",
      Authorization: `Token ${token}`,
    };
    axios
      .get("http://127.0.0.1:8000/users/all/")
      .then((res) => {
        console.log(res.data);
        dispatch(getUserListSuccess(res.data));
      })
      .catch((err) => {
        console.log(err);
      });
  };
};

export default userSlice.reducer;
