import React from "react";
import Sidebar from "../components/Sidebar";
import { useSelector, useDispatch } from "react-redux";
import { toggle } from "../features/metaSlice";
import Header from "../components/Header";

function Layout({ routes, isAuthenticated }) {
  const showSidebar = useSelector((state) => state.meta.showSidebar);
  const dispatch = useDispatch();

  return (
    <>
      <div className="app">
        {showSidebar ? <Sidebar /> : null}
        <div className="main">
          <Header />
          <button onClick={() => dispatch(toggle(!showSidebar))}>toggle</button>
        </div>
      </div>
    </>
  );
}

export default Layout;
