import React from "react";
import styles from "../styles/Sidebar.module.css";
import { useSpring, animated } from "react-spring";

const show = { width: 300, from: { width: 0 } };
const hide = { width: 0, from: { width: 300 } };

function Sidebar(props) {
  console.log(props.toggle);

  const springProps = useSpring(props.toggle ? show : hide);

  return (
    <animated.div style={springProps} className={styles.sidebar}></animated.div>
  );
  //   return <div className={styles.sidebar}></div>;
}

export default Sidebar;
