import React, { useEffect } from "react";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import { makeStyles } from "@material-ui/core/styles";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import { getGradeLevel } from "../features/schoolSlice";
import { useSelector, useDispatch } from "react-redux";

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: "25%",
  },
}));
function CreateQuiz() {
  const classes = useStyles();
  const dispatch = useDispatch();
  const { userId, token } = useSelector((state) => state.auth);
  const [level, setLevel] = React.useState("");
  const levels = useSelector((state) => state.school.gradeLevel);

  const handleChange = (event) => {
    setLevel(event.target.value);
  };

  useEffect(() => {
    dispatch(getGradeLevel(token));
  }, [token, dispatch]);

  return (
    <div className="createQuiz">
      <div className="createQuiz__level">
        <FormControl variant="outlined" className={classes.formControl}>
          <InputLabel id="select-filled-label">Grade Level</InputLabel>
          <Select
            color="primary"
            labelId="select-filled-label"
            id="select-filled"
            value={level}
            onChange={handleChange}
          >
            {levels?.map((item) => {
              return <MenuItem value={item.id}>{item.name}</MenuItem>;
            })}
          </Select>
        </FormControl>
      </div>
    </div>
  );
}

export default CreateQuiz;
