import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { createAnnouncement } from "../features/announcementSlice";
import { toggle } from "../features/updateSlice";

function CreateAnnouncement() {
  const token = useSelector((state) => state.auth.token);
  const [name, setName] = React.useState("");
  const [content, setContent] = React.useState("");
  const dispatch = useDispatch();

  const handlePost = () => {
    const formData = new FormData();
    formData.append("name", name);
    formData.append("content", content);
    formData.append("isGlobal", true);
    formData.append("classroom_id", 2);
    formData.append("author_id", 13);
    dispatch(createAnnouncement(token, formData));
    dispatch(toggle(true));
  };

  const handleContent = (e) => {
    setContent(e.target.value);
  };

  const handleName = (e) => {
    setName(e.target.value);
  };
  return (
    <div className="announcement__create">
      <div className="announcement__fields">
        <label>Title</label>
        <input onChange={handleName} />
        <label>Content</label>
        <textarea onChange={handleContent} />
      </div>

      <div className="announcement__post">
        <button onClick={handlePost}>Post Announcement</button>
      </div>
    </div>
  );
}

export default CreateAnnouncement;
