import { Button, TextField, Typography } from "@material-ui/core";
import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getQuizDetail } from "../../features/quizSlice";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
  formControl: {
    minWidth: "50%",
    margin: "10px 0",
  },
  textField: {
    minWidth: "100%",
    margin: "10px 0",
  },
  buttonStyle: {
    minWidth: "100%",
    margin: "10px 0",
    height: "3rem",
  },
}));
function QuizDetail(props) {
  const classes = useStyles();
  const dispatch = useDispatch();
  const [count, setCount] = useState(1);
  const quizDetail = useSelector((state) => state.quiz.quizDetail);
  const token = useSelector((state) => state.auth.token);

  useEffect(() => {
    dispatch(getQuizDetail(token, props.id));
  }, [dispatch, token, props.id]);

  const renderInputs = (value) => {
    const inputs = [];
    for (let i = 0; i < value; i++) {
      inputs.push(
        <div className="createQuiz__question">
          <TextField
            id="outlined-basic"
            label="Quiz Title"
            variant="outlined"
            className={classes.textField}
          />
        </div>
      );
    }
  };
  console.log(count);
  const increment = () => {
    setCount(count + 1);
  };

  return (
    <div>
      {quizDetail.name}
      <div className="createQuiz">
        <Typography variant="h6">Add Questions</Typography>
        {renderInputs(count)}
        <Button onClick={increment}>Add</Button>
      </div>
    </div>
  );
}

export default QuizDetail;
