import React, { useEffect, useState } from "react";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import Select from "@material-ui/core/Select";
import {
  getSchoolYear,
  getGradeLevel,
  getCover,
} from "../features/schoolSlice";
import { useSelector, useDispatch } from "react-redux";

function CreateClassroom() {
  const dispatch = useDispatch();
  const [cover, setCover] = useState("");
  const [year, setYear] = useState("");
  const [gradeLevel, setGradeLevel] = useState("");
  const schoolYear = useSelector((state) => state.school.schoolYear);
  const covers = useSelector((state) => state.school.cover);
  const level = useSelector((state) => state.school.gradeLevel);
  const { token, first_name, last_name } = useSelector((state) => state.auth);

  useEffect(() => {
    dispatch(getSchoolYear(token));
    dispatch(getGradeLevel(token));
    dispatch(getCover(token));
  }, []);

  const handleLevelChange = (e) => {
    console.log(e.target.value);
    setGradeLevel(e.target.value);
  };
  const handleYearChange = (e) => {
    console.log(e.target.value);
    setYear(e.target.value);
  };

  const handleCoverChange = (e) => {
    console.log(e.target.value);
    setCover(e.target.value);
  };
  return (
    <div className="classroom__create">
      <div className="classroom__name">
        <input placeholder="Name" />
        <input placeholder="Schedule" />
        <input value={`${first_name} ${last_name}`} disabled />
        <input disabled value="status: Active" />
      </div>
      <div className="classroom__details">
        <InputLabel id="level">Grade Level</InputLabel>
        <Select
          labelId="level"
          id="level"
          value={gradeLevel}
          onChange={handleLevelChange}
        >
          {level &&
            level.map((item) => {
              return (
                <MenuItem key={item.id} value={item.id}>
                  {item.name}
                </MenuItem>
              );
            })}
        </Select>
        <InputLabel id="schoolyear">School Year</InputLabel>
        <Select
          labelId="schoolyear"
          id="schoolyear"
          value={year}
          onChange={handleYearChange}
        >
          {schoolYear &&
            schoolYear.map((item) => {
              return (
                <MenuItem key={item.id} value={item.id}>
                  {item.start_year}-{item.end_year}
                </MenuItem>
              );
            })}
        </Select>
        <InputLabel id="cover">Cover</InputLabel>
        <Select
          labelId="cover"
          id="cover"
          value={cover}
          onChange={handleCoverChange}
        >
          {covers &&
            covers.map((item) => {
              return (
                <MenuItem key={item.id} value={item.id}>
                  {item.name}
                </MenuItem>
              );
            })}
        </Select>
      </div>
      <div className="classroom__buttonCreate">
        <button>ADD</button>
      </div>
    </div>
  );
}

export default CreateClassroom;
