import React, { useEffect, useState } from "react";
import ListItemAvatar from "@material-ui/core/ListItemAvatar";
import {
  Avatar,
  CircularProgress,
  TextField,
  Typography,
} from "@material-ui/core";
import FolderIcon from "@material-ui/icons/Folder";
import { getModules } from "../features/moduleSlice";
import { useDispatch, useSelector } from "react-redux";
import SystemUpdateAltIcon from "@material-ui/icons/SystemUpdateAlt";

import { makeStyles } from "@material-ui/core/styles";
const useStyles = makeStyles((theme) => ({
  formControl: {
    minWidth: "50%",
    margin: "10px 0",
  },
  textField: {
    minWidth: "100%",
    margin: "10px 0",
  },
  buttonStyle: {
    minWidth: "100%",
    margin: "10px 0",
    height: "3rem",
  },
}));

function Module() {
  const classes = useStyles();
  const dispatch = useDispatch();
  const token = useSelector((state) => state.auth.token);
  const { modules, loading, error } = useSelector((state) => state.modules);
  const [name, setName] = useState("");
  const [file, setFile] = useState(null);

  useEffect(() => {
    dispatch(getModules(token));
  }, [token, dispatch]);

  const upload = (e) => {
    setFile(e.target.files[0]);
  };

  const handleName = (e) => {
    setName(e.target.value);
  };

  console.log(file);

  return (
    <div className="modules">
      {loading ? (
        <div className="loading">
          <CircularProgress color="secondary" />
        </div>
      ) : (
        <>
          {modules?.map((item) => {
            return (
              <div className="module__item">
                <div className="module__name">
                  <ListItemAvatar>
                    <Avatar>
                      <FolderIcon />
                    </Avatar>
                  </ListItemAvatar>
                  <p>{item.name}</p>
                </div>

                <div className="module__link">
                  <SystemUpdateAltIcon />
                  <a href={`${item.module}`}>Download</a>
                </div>
                <p>{item.created}</p>
              </div>
            );
          })}
        </>
      )}
      <div className="module__upload">
        <Typography variant="h6">Upload Module</Typography>
        <div className="upload__input">
          <TextField
            id="outlined-basic"
            label="Quiz Title"
            variant="outlined"
            className={classes.textField}
            onChange={handleName}
          />
          <input onChange={upload} type="file" name="module"></input>
        </div>
      </div>
    </div>
  );
}

export default Module;
